import React, { useState, useEffect } from "react";

import { useChats } from "../hooks/useChats";
import { useAuthContext } from "../hooks/useAuthContext";

export const Chats = () => {
  const { loadChats, chats, err } = useChats();
  const { user } = useAuthContext();
  const [_chats, setChats] = useState([]);
  const [messages, setMessages] = useState([])
  useEffect(() => {
    const getChats = async() => {
      user._id && await loadChats();

      setChats([chats]);

    }

    getChats()


  }, [user._id]);

  console.log("this is it", _chats)

  const handleDateClick = (date) => {
    _chats.map((chat)=> {
      if (date === chat.date) {
        return (
          setMessages(chat.messages)

        )

      }

    })

  }

  return (
    <div className="chats">
    {_chats.map((chat) => {
      return (
        <div className="userChat" onClick={() => handleDateClick(chat.date)}>
          <img
            src="https://images.pexels.com/photos/1967902/pexels-photo-1967902.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=2"
            alt=""
          />
          <div className="userChatInfo"></div>
          <span>{chat.date}</span>
        </div>
      );
    })}
    <Messages onDateClick={handleDateClick} />
  </div>
);
}
