            // }

            if (!json.date) {
                setErr(json)
                throw new Error(JSON.stringify(json))
            }
            console.log("hello", json)
            setChats(json)



        }catch(err){
            setErr(err);
            console.log(err)

        }



    }

    return {loadChats, chats, err}
}
