export const AuthProvider = ({children}) =>{
    const [currentUser, setCurrentUser] = useState({})
    const [err, setErr] = useState();
    const [loading, setLoading] = useState(false)


    useEffect(()=>{
        localStorage
            .getItem('user')
            .then((user)=>setCurrentUser(user))
            .catch((_err)=>{})
    },[]);
}

