export const ChatContext = createContext();

export const ChatContextProvider = ({children}) => {
   const INITIAL_STATE = {
    chat_date: new Date.now(),
    _messages: {}
   }

   const chatReducer = async (state, action) => {
    //change date on date click, change the _messages, update date

    switch(action.type){
        case "CHANGE_MESSAGES":
            return{
                _messages: action.payload,
                chat_date: await loadChats().date


            }
            default:
                return state;
    }
   }
    return (
        <ChatContext.Provider>
            {children}
        </ChatContext.Provider>
    )
}
