import { useLogin } from '../hooks/useLogin'


export const Home = async() => {
  const {checkLogin, loggedin} = useLogin()
  await checkLogin()


  return (
    <div className='home'>
      {loggedin && <div className="container">
        <Sidebar/>
        <Chat/>


      </div>}

    </div>
  )
}
